leases = msg.payload;
raw = msg.stations.trim().split("\n")
let stations = [];
let rssi = 0;
let mac = "";

if(raw.length >= 2) {
    for(let i=0; i<raw.length;i+=2) {
        rssi = parseInt(raw[i+1]);
        mac = raw[i];
        lease = leases.find(item => item.mac === mac);
        stations.push({
            mac, 
            ip: lease.ip,
            host: lease.hostname,
            rssi,
            rssi_Percent: rssi >= -50 ? 100 : (rssi <= -100 ? 0 : 2 * (rssi + 100)),
        });
    }
}

msg.payload = {
    connected: stations.length,
    stations
}
return msg;
